mapper.select:
		SELECT
		
		FROM td_sm_job
		
			
	          job_id = #{jobEO.jobId,jdbcType=VARCHAR}
			
			
	          AND job_name like '%' || #{jobEO.jobName,jdbcType=VARCHAR} || '%' 
			
			
	          AND job_desc like '%' || #{jobEO.jobDesc,jdbcType=VARCHAR} || '%' 
			
			
	          AND job_scope = #{jobEO.jobScope,jdbcType=CHAR}
			

			
	          AND job_function = #{jobEO.jobFunction,jdbcType=VARCHAR}
			
			
	          AND job_amount = #{jobEO.jobAmount,jdbcType=VARCHAR}
			
			
	          AND job_number = #{jobEO.jobNumber,jdbcType=VARCHAR}
			
			
	          AND job_condition = #{jobEO.jobCondition,jdbcType=VARCHAR}
			

			
	          AND job_rank = #{jobEO.jobRank,jdbcType=VARCHAR}
			
			
	          AND owner_id = #{jobEO.ownerId,jdbcType=VARCHAR}
			
		
		order by job_scope,job_name,job_id

		SELECT t.* FROM td_sm_job t LEFT JOIN td_sm_orgjob t1 ON t.job_id =
		t1.job_id
		
			
          t1.org_id = #{orgId,jdbcType=VARCHAR}
			

			
          AND t.job_id = #{jobEO.jobId,jdbcType=VARCHAR}
			
			
          AND t.job_name like '%' || #{jobEO.jobName,jdbcType=VARCHAR} || '%' 
			
			
          AND t.job_desc like '%' || #{jobEO.jobDesc,jdbcType=VARCHAR} || '%' 
			
			
          AND t.job_scope = #{jobEO.jobScope,jdbcType=CHAR}
			

			
          AND t.job_function = #{jobEO.jobFunction,jdbcType=VARCHAR}
			
			
          AND t.job_amount = #{jobEO.jobAmount,jdbcType=VARCHAR}
			
			
          AND t.job_number = #{jobEO.jobNumber,jdbcType=VARCHAR}
			
			
          AND t.job_condition = #{jobEO.jobCondition,jdbcType=VARCHAR}
			

			
          AND t.job_rank = #{jobEO.jobRank,jdbcType=VARCHAR}
			
			
          AND t.owner_id = #{jobEO.ownerId,jdbcType=VARCHAR}
			
		
		order by t.job_scope,t.job_name,t.job_id

		SELECT
		
		FROM td_sm_job t
		
	 	
       		job_id IN (SELECT ui.scope_id
	                      FROM td_sm_userinstance ui
	                     WHERE ui.scope_type = '2'
	                       AND ui.user_id = #{userId,jdbcType=VARCHAR})   
	    
			
          AND t.job_id = #{jobEO.jobId,jdbcType=VARCHAR}
			
			
          AND t.job_name like '%' || #{jobEO.jobName,jdbcType=VARCHAR} || '%' 
			
			
          AND t.job_desc like '%' || #{jobEO.jobDesc,jdbcType=VARCHAR} || '%' 
			
			
          AND t.job_scope = #{jobEO.jobScope,jdbcType=CHAR}
			

			
          AND t.job_function = #{jobEO.jobFunction,jdbcType=VARCHAR}
			
			
          AND t.job_amount = #{jobEO.jobAmount,jdbcType=VARCHAR}
			
			
          AND t.job_number = #{jobEO.jobNumber,jdbcType=VARCHAR}
			
			
          AND t.job_condition = #{jobEO.jobCondition,jdbcType=VARCHAR}
			

			
          AND t.job_rank = #{jobEO.jobRank,jdbcType=VARCHAR}
			
			
          AND t.owner_id = #{jobEO.ownerId,jdbcType=VARCHAR}

SELECT tt.* FROM td_sm_role tt INNER JOIN td_sm_grouprole tt1 ON tt.role_id = tt1.role_id AND tt.role_usage = '1' INNER JOIN td_sm_group tt2 ON tt1.group_id = tt2.group_id INNER JOIN td_sm_grouporgjob tt3 ON tt2.group_id = tt3.group_id INNER JOIN td_sm_job tt4 ON tt3.job_id = tt4.job_id
tt4.job_id = #{jobId,jdbcType=VARCHAR}
AND tt.role_type = #{anonymousRoleType,jdbcType=VARCHAR}

