SELECT COUNT(*) FROM td_sm_organization t LEFT JOIN td_sm_orgjob t1 ON
t.org_id = t1.org_id
WHERE t1.job_id = #{jobId,jdbcType=VARCHAR}
AND t.org_id = #{orgId,jdbcType=VARCHAR}

