SELECT
FROM (SELECT log.log_id, log.log_operuser, CASE WHEN u.user_realname IS NULL THEN log.log_operuser ELSE u.user_realname END AS log_operuserrealname, log.oper_module, lc.log_type, lc.log_oper, lc.log_operdesc, log.log_visitorial, log.log_opertime, log.log_content, log.oper_type, log.log_status, log.remark1, log.remark2, log.remark3, log.remark4, log.remark5 FROM ${tableName} log LEFT JOIN td_sm_user u ON log.log_operuser = u.user_name LEFT JOIN td_sm_logconfig lc ON log.oper_module = lc.oper_module )
log_id = #{logId,jdbcType=VARCHAR}
AND log_operUser = #{logEo.logOperUser,jdbcType=VARCHAR}
AND log_operuserrealname like '%' || #{logEo.logOperUserRealName,jdbcType=VARCHAR} || '%'
AND oper_module = #{logEo.operModule,jdbcType=VARCHAR}
AND log_type = #{logEo.logType,jdbcType=VARCHAR}
AND log_oper = #{logEo.logOper,jdbcType=VARCHAR}
AND log_operdesc like '%' || #{logEo.logOperdesc,jdbcType=VARCHAR} || '%'
AND log_visitorial like '%' || #{logEo.logVisitorial,jdbcType=VARCHAR} || '%'
AND log_content like '%' || #{logEo.logContent,jdbcType=VARCHAR} || '%'
AND oper_type = #{logEo.operType,jdbcType=CHAR}
AND log_status = #{logEo.logStatus,jdbcType=CHAR}
AND remark1 like '%' || #{logEo.remark1,jdbcType=VARCHAR} || '%'
AND remark2 like '%' || #{logEo.remark2,jdbcType=VARCHAR} || '%'
AND remark3 like '%' || #{logEo.remark3,jdbcType=VARCHAR} || '%'
AND remark4 like '%' || #{logEo.remark4,jdbcType=VARCHAR} || '%'
AND remark5 like '%' || #{logEo.remark5,jdbcType=VARCHAR} || '%'
AND to_date(to_char(log_opertime,'yyyy-mm-dd hh24:mi:ss'),'yyyy-mm-dd hh24:mi:ss') >= to_date(to_char(#{paramMap.startDate,jdbcType=VARCHAR},'yyyy-mm-dd hh24:mi:ss'),'yyyy-mm-dd hh24:mi:ss')
AND to_date(to_char(log_opertime,'yyyy-mm-dd hh24:mi:ss'),'yyyy-mm-dd hh24:mi:ss') <= to_date(to_char(#{paramMap.endDate,jdbcType=VARCHAR},'yyyy-mm-dd hh24:mi:ss'),'yyyy-mm-dd hh24:mi:ss')
ORDER BY log_opertime DESC
