mapper.select:
SELECT u.*
FROM td_sm_user u


		u.user_id in(
		SELECT user_id 
		FROM td_sm_userinstance 
		WHERE scope_type = '2' 
		AND scope_id = #{jobId,jdbcType=VARCHAR}
		)				


		AND u.user_id = #{userEO.userId,jdbcType=VARCHAR}


		AND u.user_name like '%' ||
		#{userEO.userName,jdbcType=VARCHAR} || '%'


		AND u.user_password =
		#{userEO.userPassword,jdbcType=VARCHAR}


		AND u.user_realname LIKE '%' ||
		#{userEO.userRealname,jdbcType=VARCHAR} || '%'


		AND u.user_pinyin LIKE '%' ||
		#{userEO.userPinyin,jdbcType=VARCHAR} || '%'


		AND u.user_sex = #{userEO.userSex,jdbcType=VARCHAR}


		AND u.user_hometel LIKE '%' ||
		#{userEO.userHometel,jdbcType=VARCHAR} || '%'


		AND u.user_worktel LIKE '%' ||
		#{userEO.userWorktel,jdbcType=VARCHAR} || '%'


		AND u.user_workaddress LIKE '%' ||
		#{userEO.userWorkaddress,jdbcType=VARCHAR} || '%'


		AND u.user_mobiletel1 LIKE '%' ||
		#{userEO.userMobiletel1,jdbcType=VARCHAR} || '%'


		AND u.user_mobiletel2 LIKE '%' ||
		#{userEO.userMobiletel2,jdbcType=VARCHAR} || '%'


		AND u.user_fax LIKE '%' ||
		#{userEO.userFax,jdbcType=VARCHAR} || '%'


		AND u.user_oicq LIKE '%' ||
		#{userEO.userOicq,jdbcType=VARCHAR} || '%'


		AND u.user_birthday =
		#{userEO.userBirthday,jdbcType=DATE}


		AND u.user_email = #{userEO.userEmail,jdbcType=VARCHAR}


		AND u.user_address LIKE '%' ||
		#{userEO.userAddress,jdbcType=VARCHAR} || '%'


		AND u.user_postalcode =
		#{userEO.userPostalcode,jdbcType=VARCHAR}


		AND u.user_idcard = #{userEO.userIdcard,jdbcType=VARCHAR}


		AND u.user_isvalid =
		#{userEO.userIsvalid,jdbcType=DECIMAL}


		AND u.user_regdate = #{userEO.userRegdate,jdbcType=DATE}


		AND u.user_logincount =
		#{userEO.userLogincount,jdbcType=DECIMAL}


		AND u.user_type = #{userEO.userType,jdbcType=VARCHAR}


		AND u.past_time = #{userEO.pastTime,jdbcType=DATE}


		AND u.dredge_time = #{userEO.dredgeTime,jdbcType=VARCHAR}


		AND u.lastlogin_date =
		#{userEO.lastloginDate,jdbcType=DATE}


		AND u.worklength = #{userEO.worklength,jdbcType=VARCHAR}


		AND u.politics = #{userEO.politics,jdbcType=VARCHAR}


		AND u.login_ip = #{userEO.loginIp,jdbcType=VARCHAR}


		AND u.cert_sn = #{userEO.certSn,jdbcType=VARCHAR}


		AND u.user_sn = #{userEO.userSn,jdbcType=DECIMAL}


		AND u.remark1 = #{userEO.remark1,jdbcType=VARCHAR}


		AND u.remark2 = #{userEO.remark2,jdbcType=VARCHAR}


		AND u.remark3 = #{userEO.remark3,jdbcType=VARCHAR}


		AND u.remark4 = #{userEO.remark4,jdbcType=VARCHAR}


		AND u.remark5 = #{userEO.remark5,jdbcType=VARCHAR}


		AND u.remark6 = #{userEO.remark6,jdbcType=VARCHAR}


		AND u.remark7 = #{userEO.remark7,jdbcType=VARCHAR}


		AND u.remark8 = #{userEO.remark8,jdbcType=VARCHAR}


		AND u.remark9 = #{userEO.remark9,jdbcType=VARCHAR}


